mapper.update:
UPDATE Donation
SET enable = 1
WHERE donation_id = #{donationId}

UPDATE Talent
SET enable = 1
WHERE talent_id = #{talentId}

mapper.select:
SELECT file fileName
FROM MarketFile
WHERE market_id = #{marketId}

mapper.update:
UPDATE Market
SET title = #{title},
	content = #{content},
	product_name = #{productName},
	price = #{price}
WHERE market_id = #{marketId}

mapper.delete:
DELETE FROM MarketFile
WHERE market_id = #{marketId}
	AND file = #{file}
