mapper.select:
SELECT profile
FROM Member
WHERE member_id = #{memberId}

mapper.update:
UPDATE Donation
SET enable = 1
WHERE donation_id = #{donationId}

UPDATE Talent
SET enable = 1
WHERE talent_id = #{talentId}

mapper.select:
SELECT member_id
FROM MeetingGuest
WHERE meeting_id = #{meetingId}

SELECT file fileName
FROM MarketFile
WHERE market_id = #{marketId}

mapper.update:
UPDATE Market
SET title = #{title},
	content = #{content},
	product_name = #{productName},
	price = #{price}
WHERE market_id = #{marketId}

mapper.delete:
DELETE FROM MarketFile
WHERE market_id = #{marketId}
	AND file = #{file}

mapper.select:
SELECT file fileName
FROM FaqFile
WHERE question_id = #{questionId}

mapper.update:
UPDATE Faq
SET enable = 1
WHERE question_id = #{questionId}
