mapper.select:
SELECT COUNT(member_id) 
FROM Member 
WHERE member_id = #{memberId}

SELECT COUNT(email) 
FROM Member 
WHERE email = #{email}

SELECT COUNT(nickname)
FROM Member 
WHERE nickname = #{nickname}

SELECT COUNT(member_id)
FROM Member
WHERE name = #{name} AND email = #{email}

SELECT member_id
FROM Member
WHERE name = #{name} AND email = #{email}

SELECT member_id memberId, pw, name, email, nickname, postcode, address, detail, phone, profile
FROM Member
WHERE member_id = #{memberId}

mapper.update:
UPDATE Member SET email = #{email}, nickname = #{nickname}, postcode = #{postcode}, address = #{address}, detail = #{detail},
profile = #{profile},
phone = #{detail} WHERE member_id = #{memberId}

