mapper.select:
SELECT COUNT(member_id) 
FROM Member 
WHERE member_id = #{memberId}

SELECT COUNT(email) 
FROM Member 
WHERE email = #{email}

SELECT COUNT(nickname)
FROM Member 
WHERE nickname = #{nickname}

SELECT COUNT(member_id)
FROM Member
WHERE name = #{name} AND email = #{email}

SELECT member_id
FROM Member
WHERE name = #{name} AND email = #{email}

SELECT member_id, pw, name, email, nickname, postcode, address, detail, birth, phone
	from Member
	WHERE member_id = #{memberId}
